import"./hoisted.BNuLwCWa.js";document.addEventListener("click",async n=>{const t=n.target;if(!t.classList.contains("update-button"))return;const e=t.closest(".game-card"),a=e.dataset.gameId,o=e.querySelectorAll(".team-score-input"),s=o[0].value,c=o[1].value;t.textContent="Updating...",t.setAttribute("disabled","true");try{if(!(await fetch(`/api/games/${a}`,{method:"PATCH",headers:{"Content-Type":"application/json"},body:JSON.stringify({ehsFinal:parseInt(s,10),oppFinal:parseInt(c,10)})})).ok)throw new Error("Failed to update score");t.textContent="Updated!",setTimeout(()=>{t.textContent="Update Score",t.removeAttribute("disabled")},2e3)}catch(r){console.error("Update Error:",r),t.textContent="Error!",t.style.backgroundColor="#d9534f",setTimeout(()=>{t.textContent="Update Score",t.removeAttribute("disabled"),t.style.backgroundColor=""},3e3)}});
